import React from 'react';
import axios from 'axios';
import Movie from './Movie'
class App extends React.Component {
  state = {
    isLoading: true,
    movies: [],
  };

  //프로미스(Promise)의 3가지 상태(states)
  //Pending(대기) : 비동기 처리 로직이 아직 완료되지 않은 상태
  //Fulfilled(이행) : 비동기 처리가 완료되어 프로미스가 결과 값을 반환해준 상태
  //Rejected(실패) : 비동기 처리가 실패하거나 오류가 발생한 상태

  getMovies = async () => {
    const {
      data: {
        data: { movies },
      },
    }= await axios.get('https://yts.mx.now.sh/list_movies.json?sort_by=rating');
    this.setState({ movies, isLoading: false });
  };

  componentDidMount() {
    this.getMovies();
  }

  render() {
    const { isLoading, movies } = this.state;
    return <div>
      { isLoading 
      ? 'Loading...' //삼항연산자 isLoading이 true이면 Loading...출력 false이면 Movie 컴포넌트에 props 전달하여 반환
      : movies.map((movie) => {
          console.log(movie);
          return <Movie 
          id={movie.id}
          />;
      })}
      </div>;
  }
}

export default App;
